-- Create table for Indian Stocks
create table if not exists public.indian_stocks (
    id uuid default gen_random_uuid() primary key,
    symbol text not null unique,
    name text not null,
    current_price numeric,
    price_change_percentage_24h numeric,
    market_cap numeric,
    high_24h numeric,
    low_24h numeric,
    volume numeric,
    image text, -- URL to logo
    updated_at timestamp with time zone default timezone('utc'::text, now()) not null
);

-- Enable RLS
alter table public.indian_stocks enable row level security;

-- Create policy to allow read access for everyone
drop policy if exists "Allow public read access" on public.indian_stocks;
create policy "Allow public read access"
    on public.indian_stocks for select
    using (true);

-- Seed Data (250 Mock Indian Stocks)
on conflict (symbol) do nothing;
